import './MovieItem.css';

function MovieItem({ movie, onMovieSelect }) {
  return (
    <div className='movie-container' key={movie.episode_id}>
      <img src={require("/Users/galkatz/Desktop/ðŸ’»/VSCode/ps-assignment-main/src/images/1.jpg")} alt="movie image" className='movie-image'>
      <button onClick={() => onMovieSelect(movie)} className='movie-btn'>
        View {movie.title}
        </button>
        </img>
    </div>
  );
}

export default MovieItem;
